create table "motorbike-garages" (id varchar(255) not null, motorbike_fuel_type varchar(255), motorbike_make varchar(255), motorbike_mileage varchar(255), motorbike_model varchar(255), motorbike_transmission varchar(255), primary key (id));
create table passwordresettokens (expiration_time timestamp(6), id varchar(255) not null, token varchar(255), user_id varchar(255) not null unique, primary key (id));
create table products (id serial not null, price float(53) not null, created_at timestamp(6), brand varchar(255), category varchar(255), description TEXT, image_url_name varchar(255), name varchar(255), primary key (id));
create table users (is_enabled boolean not null, role smallint check (role between 0 and 3), created_at timestamp(6), updated_at timestamp(6), about TEXT, email varchar(255), id varchar(255) not null, name varchar(255), password varchar(255), user_name varchar(255), primary key (id));
create table "vehicle-garages" (vehicle_mileage integer not null, id varchar(255) not null, vehicle_fuel_type varchar(255), vehicle_make varchar(255), vehicle_model varchar(255), vehicle_transmission varchar(255), primary key (id));
create table verificationtokens (expiration_time timestamp(6), id varchar(255) not null, token varchar(255), user_id varchar(255) not null unique, primary key (id));
create table wheelies (buying_price float(53) not null, previous_owners_count integer not null, created_at timestamp(6), updated_at timestamp(6), description TEXT, id varchar(255) not null, name varchar(255), user_id varchar(255), primary key (id));